    NO SQL
    DETERMINISTIC
BEGIN
DECLARE latitude_commune_ref FLOAT;
	DECLARE longitude_commune_ref FLOAT;
	DECLARE lon1 FLOAT;
	DECLARE lon2 FLOAT;
	DECLARE lat1 FLOAT;
	DECLARE lat2 FLOAT;
	


	
	SELECT lat,lng INTO latitude_commune_ref,longitude_commune_ref FROM ric_communes as communes WHERE nom = commune_ref LIMIT 1;
	SET lon1 = longitude_commune_ref - distance_commune/abs(cos(radians(latitude_commune_ref))*111.1);
	SET lon2 =longitude_commune_ref+distance_commune/abs(cos(radians(latitude_commune_ref))*111.1);
	SET lat1 =latitude_commune_ref -(distance_commune/111.1);
	SET lat2=latitude_commune_ref+(distance_commune/111.1);
	SELECT GROUP_CONCAT(id) as communes_list
		 FROM ric_communes  as communes
		
		WHERE communes.lng BETWEEN lon1 and lon2
		AND communes.lat BETWEEN lat1 and lat2
		AND (6371 *2 * ASIN(SQRT(POWER(SIN((latitude_commune_ref-lat)*pi()/180/2),2)+COS(latitude_commune_ref+pi()/180)*COS(lat*pi()/180)*POWER(SIN((longitude_commune_ref-lng)*pi()/180/2),2)))) <distance_commune;
END$$

